<script setup lang="ts">
import {onMounted, ref} from "vue";
import {User} from "@/object/UserProfile";
import {HTTPAxios} from "@/object/utils/HTTPAxios";
import AppearAnimation from "@/vue/global/utils/AppearAnimation.vue";
import axios from "axios";

const user = ref<User>()
const isOnScreen = ref(false)
const emits = defineEmits(['on-loaded'])
const logoUrl = ref<string>("")
onMounted(() => {
  new HTTPAxios("user/user.json", null, true).get().then((data) => {
    user.value = data.data as User
    emits('on-loaded')
  })
  axios.get("https://raw.githubusercontent.com/" + import.meta.env.VITE_GITHUB_HOST + "/main/.assets/logo/logo.svg").then((data) => {
    logoUrl.value = data.data
  })
})
</script>

